CREATE TABLE `roi_settings` (
  `id` int NOT NULL DEFAULT '1',
  `cra_monthly_salary` decimal(10,2) DEFAULT '60000.00',
  `cra_daily_expense` decimal(10,2) DEFAULT '500.00',
  `sdc_monthly_salary` decimal(10,2) DEFAULT '45000.00',
  `investigator_fee` decimal(10,2) DEFAULT '150.00',
  `exam_fee` decimal(10,2) DEFAULT '100.00',
  `patient_travel_fee` decimal(10,2) DEFAULT '75.00',
  `turp_daily_license` decimal(10,2) DEFAULT '8.00',
  `trad_cra_minutes` int DEFAULT '120',
  `trad_sdc_minutes` int DEFAULT '90',
  `turp_cra_minutes` int DEFAULT '15',
  `turp_sdc_minutes` int DEFAULT '10',
  `usd_rate` decimal(10,4) DEFAULT '35.5000',
